import React, { useEffect, useState } from "react";
import {
  View,
  ScrollView,
  TouchableOpacity,
  Dimensions,
  FlatList,
  Image,
  Alert,
  Pressable,
} from "react-native";
import { Text } from "react-native";
import { bigIcon } from "../../assets/icon";
import { SvgXml } from "react-native-svg";
import { FontAwesome } from "@expo/vector-icons";
import { Color } from "../../assets/colors";
import Button from "./../../components/Button";
const { height, width } = Dimensions.get("window");
import { useSelector, useDispatch } from "react-redux";
import {
  getGigs,
  getDashboard,
  getServiceMessageCount,
} from "../../Class/service";
import { vendorLogin } from "../../Class/auth";
import { ActivityIndicator } from "react-native-paper";
import { useSafeAreaInsets } from "react-native-safe-area-context";
import IconButton from "../../components/IconButton";
import Frame from "../../assets/Images/Frame.png";
import { useIsFocused } from "@react-navigation/native";
import { setHideBottomBar } from "../../Reducers/hideBottomBar";

const DashboardList = ({ navigation, route }) => {
  const vendorInfo = useSelector((state) => state.vendorInfo);
  const user = useSelector((state) => state.user);
  const [Data, setData] = React.useState();
  const dispatch = useDispatch();
  const [Loading, setLoading] = React.useState(false);
  const isDark = useSelector((state) => state.isDark);
  const colors = new Color(isDark);
  const primaryColor = colors.getPrimaryColor();
  const textColor = colors.getTextColor();
  const secondaryColor = colors.getSecondaryColor();
  const backgroundColor = colors.getBackgroundColor();
  const data = route?.params?.data;
  const vendorOrders = useSelector((state) => state.vendorOrders);
  const inset = useSafeAreaInsets();
  const isFocused = useIsFocused();

  React.useEffect(() => {
    if (user) {
      setLoading(true);
      getDashboard(user.token)
        .then((result) => {
          if (result && result.data && result.data.dashboards) {
            dispatch({
              type: "SET_VENDOR_INFO",
              playload: result.data.dashboards,
            });
            setLoading(false);
            setData(result.data.dashboards);
          } else {
            dispatch({ type: "SET_VENDOR_INFO", playload: false });
            setLoading(false);
          }
        })
        .catch((err) => {
          setLoading(false);
        });
    }
  }, [isFocused]);
  React.useEffect(() => {
    if (isFocused) {
      //console.log("hidden")
      dispatch(setHideBottomBar(false));
      setTimeout(() => {
        dispatch(setHideBottomBar(false));
      }, 50);
    } else {
      //console.log("seen")
      //dispatch(setHideBottomBar(true));
    }
  }, [isFocused]);

  if (Loading) {
    return (
      <View
        style={{
          flex: 1,
          justifyContent: "center",
          alignItems: "center",
        }}>
        <ActivityIndicator size="small" color={backgroundColor} />
      </View>
    );
  }
  const click = (id) => {
    setLoading(true);
    dispatch({ type: "VENDOR_ORDERS", playload: null });
    vendorLogin(user.token, id).then((res) => {
      if (res) {
        //console.log(res)
        setLoading(false);
        dispatch({ type: "SET_VENDOR", playload: res });
        //navigation.navigate("Profile");
        navigation.navigate("MainProfile");
      } else {
        setLoading(false);
        Alert.alert("Problem in log into dashboard");
      }
    });
  };
  return (
    <View
      style={{
        paddingTop: inset?.top,
        flex: 1,
      }}>
      <ScrollView showsVerticalScrollIndicator={false}>
        <View
          style={{
            paddingTop: 20,
            alignItems: "center",

            justifyContent: "center",
          }}>
          <SvgXml xml={icon} />
        </View>
        <Image
          style={{ marginTop: 32, width: "100%", height: 260 }}
          source={Frame}
        />
        <View style={{ height: 32 }} />
        <Text
          style={{
            fontSize: 24,

            fontWeight: "500",
            marginHorizontal: 20,
          }}>
          Login or create your business account
        </Text>
        <View
          style={{
            justifyContent: "center",
            alignItems: "center",
          }}
          showsHorizontalScrollIndicator={false}
          horizontal={true}>
          <View style={{ height: 32 }} />
          {Data &&
            Data.map((doc, i) => <Cart key={i} onChange={click} data={doc} />)}
          {Data && Data.length == 0 && (
            <Text
              style={{
                fontSize: 16,
                marginTop: 30,
                marginBottom: 38,
                width: "100%",
                textAlign: "center",
              }}>
              No account found
            </Text>
          )}
          {/* <Cart onChange={click} data={data} /> */}
          <View style={{ height: 24 }} />
        </View>

        <IconButton
          LeftIcon={() => <SvgXml xml={plus} />}
          onPress={() => {
            dispatch({ type: "SET_NEW_DATA", playload: null });
            navigation.navigate("ServiceCategory");
          }}
          style={{
            borderWidth: 0,
            color: textColor,
            fontSize: 16,
            fontWeight: "400",

            marginVertical: 28,
          }}
          title={"Create an another business account"}
        />
        {/* <Button
        onPress={() => {
          navigation.goBack()
        }}
        style={{
          borderWidth: 1,
          color: textColor,
          alignSelf: "center",
        }}
        title={"Back"}
      /> */}
      </ScrollView>
    </View>
  );
};

export default DashboardList;
const Cart = ({ data, onChange }) => {
  const id = data.id;
  const image = data.image;
  const title = data.name;
  const isDark = useSelector((state) => state.isDark);
  const colors = new Color(isDark);
  const primaryColor = colors.getPrimaryColor();
  const textColor = colors.getTextColor();
  const secondaryColor = colors.getSecondaryColor();
  const backgroundColor = colors.getBackgroundColor();
  const user = useSelector((state) => state.user);
  const [count, setCount] = useState(0);
  useEffect(() => {
    if (user && id) {
      getServiceMessageCount(user?.token, id)
        .then((res) => {
          setCount(res.data.count);
        })
        .catch((e) => {
          console.log(e.response.data.msg);
        });
    }
  }, []);
  return (
    <TouchableOpacity
      onPress={() => {
        if (onChange) {
          onChange(id);
        }
      }}>
      <View
        style={{
          flexDirection: "row",
          width: width - 40,
          backgroundColor: primaryColor,
          paddingVertical: 12,
          alignItems: "center",
        }}>
        <View
          style={{
            backgroundColor: "#e5e5e5",
            borderRadius: 30,
            height: 60,
            width: 60,
            justifyContent: "center",
            alignItems: "center",
            marginRight: 12,
            overflow: "hidden",
          }}>
          {image ? (
            <Image
              style={{
                height: 60,
                width: 60,
                borderRadius: 30,
              }}
              source={{ uri: image }}
            />
          ) : (
            <FontAwesome name="user" size={40} color={backgroundColor} />
          )}
        </View>
        <View style={{ flex: 1, justifyContent: "center" }}>
          <Text
            numberOfLines={1}
            style={{
              fontSize: 16,
              fontWeight: "400",
              color: "#1A1A1A",
            }}>
            {title ? title : "Sazzad It Center"}
          </Text>
        </View>
        {count > 0 && (
          <Pressable
            style={{
              marginLeft: 20,
            }}>
            <View
              style={{
                width: 12,
                height: 12,
                backgroundColor: "#4ADE80",
                justifyContent: "center",
                alignItems: "center",
                borderRadius: 6,
                borderWidth: 1,
                borderColor: "#4ADE80",
                position: "absolute",
                top: -4,
                zIndex: 100,
                right: -3,
              }}>
              <Text
                style={{
                  fontSize: 8,
                  color: "#fff",
                }}>
                {count}
              </Text>
            </View>
            <SvgXml xml={msg} />
          </Pressable>
        )}
      </View>
    </TouchableOpacity>
  );
};

